import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function Additionaldetails() {
  const [plantDetails, setPlantDetails] = useState(null);
  const { id } = useParams(); 

  useEffect(() => {
    
    fetch(`http://localhost:8081/plantdetails/${id}`)
      .then(res => res.json())
      .then(data => setPlantDetails(data))
      .catch(err => console.log(err));
  }, [id]);

  if (!plantDetails) {
    return <div>Loading...</div>;
  }

  return (
    <div className="p-4">
      <h1 className="text-2xl font-semibold mb-4">Plant Details</h1>
      <div className="p-4 border border-gray-300 rounded-lg bg-gray-50">
        <h2 className="font-semibold text-xl">Name: {plantDetails.name}</h2>
        <p className="mt-2">Scientific Name: {plantDetails.scientific_name}</p>
        
      </div>
    </div>
  );
}

export default Additionaldetails;
